f(dp));&#x2F;&#x2F; for(int i&#x3D;0;i&lt;16;i++) solve(1ll*0xffffffff,i); int t&#x3D;read(); while(t--)&#123; int n&#x3D;read();n--; LL nn &#x3D; 0; for(int i&#x3D;0;i&lt;8;i++) nn&#x3D;(nn&lt;&lt;4)+getnum(getchar()); LL n16 &#x3D; 0xffffffff; LL ans &#x3D; 0; if(nn+n&lt;&#x3D;n16)&#123; for(int i&#x3D;2;i&lt;&#x3D;7;i++) ans+&#x3D;(solve(nn+n,i)-solve(nn-1,i))*i; &#125; else &#123; for(int i&#x3D;2;i&lt;&#x3D;7;i++) ans+&#x3D;(solve(n16,i)-solve(nn-1,i))*i; for(int i&#x3D;2;i&lt;&#x3D;7;i++) ans+&#x3D;solve(nn+n-n16-1,i)*i; &#125; printf(&quot;%lld
&quot;,ans); &#125; return 0;&#125; ZOJ 3963 F Heap Partition———————————————————————————————————————————— 现在有一个序列，让你把它分成几个集合，使得每个集合中的数能被放到最大堆上，其父子节点关系满足$s_j ≤ s_i and j &lt; i$ 首先很容易想到，就是找这个数前面有没有比它小的，有就作为这个数的儿子就好了，注意的是一个节点只有两个儿子，明确这个，随便搞搞就行了。 （其实我这个并查集是不用的，只需要写个数组映射过去就好，当时纯属脑抽 附本题代码———————————————————————————————————————————— # include &lt;bits&#x2F;stdc++.h&gt;using namespace std;const int N &#x3D; 100000+7000;int a[N],h[N];int n;int sum[N];# define lowbit(x) (x&amp;-x)void update(int i,int v)&#123; for(;i&lt;&#x3D;n;i+&#x3D;lowbit(i)) sum[i]+&#x3D;v;&#125;int getSum(int i)&#123; int ans &#x3D; 0; for(;i;i-&#x3D;lowbit(i)) ans+&#x3D;sum[i]; return ans;&#125;int BS(int x)&#123; int l&#x3D;1,r&#x3D;n,mid,ans; while(l&lt;&#x3D;r)&#123; mid &#x3D; r+l &gt;&gt;1; if(getSum(mid)&gt;&#x3D;x) r&#x3D;mid-1,ans &#x3D; mid; else l&#x3D;mid+1; &#125; return ans;&#125;int pre[N];int findi(int x)&#123; int r &#x3D; x; while(r!&#x3D;pre[r])r&#x3D;pre[r]; int i&#x3D;x,j; while(i!&#x3D;r)&#123; j&#x3D;pre[i]; pre[i]&#x3D;r; i&#x3D;j; &#125; return r;&#125;void join(int x,int y)&#123; int fx&#x3D;findi(x),fy&#x3D;findi(y); pre[fy]&#x3D;fx;&#125;vector&lt;int &gt;G[N];int main()&#123; int _; scanf(&quot;%d&quot;,&amp;_); while(_--)&#123; int ans &#x3D; 0,x,y; scanf(&quot;%d&quot;,&amp;n); for(int i&#x3D;1;i&lt;&#x3D;n;i++) pre[i]&#x3D;i; for(int i&#x3D;1;i&lt;&#x3D;n;i++)&#123; scanf(&quot;%d&quot;,&amp;a[i]); int flag &#x3D; getSum(a[i]); if(flag)&#123; x &#x3D; BS(flag); update(x,-1); join(h[x],i); &#125; update(a[i],2); h[a[i]]&#x3D;i; &#125;&#x2F;&#x2F; for(int i&#x3D;1;i&lt;&#x3D;n;i++)&#123;&#x2F;&#x2F; printf(&quot;%d %d
