00007];# define lowbit(x) (x&amp;-x)void update(int i,int v)&#123;for(;i&lt;&#x3D;n;i+&#x3D;lowbit(i))sum[i]+&#x3D;v;&#125;int getSum(int i)&#123;int ans&#x3D;0;for(;i;i-&#x3D;lowbit(i))ans+&#x3D;sum[i];return ans;&#125;int main()&#123; scanf(&quot;%d&quot;,&amp;n); for(int i&#x3D;1;i&lt;&#x3D;n;i++) scanf(&quot;%d&quot;,&amp;a[i]); scanf(&quot;%d&quot;,&amp;Q); for(int i&#x3D;1;i&lt;&#x3D;Q;i++) scanf(&quot;%d%d&quot;,&amp;q[i].l,&amp;q[i].r),q[i].id&#x3D;i; sort(q+1,q+1+Q,cmp); for(int i&#x3D;1,j&#x3D;1;i&lt;&#x3D;n&amp;&amp;j&lt;&#x3D;Q;i++)&#123; if(vis[a[i]]) update(vis[a[i]],-1); vis[a[i]]&#x3D;i; update(i,1); while(j&lt;&#x3D;Q&amp;&amp;q[j].r&lt;&#x3D;i)&#123; ans[q[j].id]&#x3D;getSum(q[j].r)-getSum(q[j].l-1); j++; &#125; &#125; for(int i&#x3D;1;i&lt;&#x3D;Q;i++)printf(&quot;%d
